CREATE TABLE players (
	player_id VARCHAR(50) PRIMARY KEY,
    first_name VARCHAR(30),
    last_name VARCHAR(30),
    short_code VARCHAR(3),
    position VARCHAR(1),
    contract_status VARCHAR(10),
    contract_end VARCHAR(10),
    expiry_status VARCHAR(3),
    years_left_current INTEGER(2),
    aav_current INTEGER(8),
    years_left_next INTEGER(2),
    aav_next INTEGER(8)
);



